        // Use this to uniquely identify each
        //counter
        allcomponents:[
            {}
        ]
     };
    render() { 
        return ( 
        //this div is the wrapper or the container for allcomponents
        <div>
            <Counter />
            <Counter />
            <Counter />
            <Counter />
        </div> 
        );
    }
}

export default Allcomponents;
