import React, { Component } from "react";
import FormSearch from "./formsearch";
import { Button } from "react-bootstrap";

class NavBar extends Component {
  render() {
    return (
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark ">
        <a className="navbar-brand mr-auto mr-lg-0" href="#">
          Home
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="d-flex flex-wrap justify-content-center position-absolute w-100 h-100 align-items-center align-content-center">
          <span className={`h1 mb-4 w-100 text-center text-${this.themeClass}`}>
            {this.theme || "Default"}
          </span>

          <div className="btn-group">
            <button
              type="button"
              className={`btn btn-${this.themeClass} btn-lg`}
            >
              {this.theme || "Choose"} Theme
            </button>

            <button
              type="button"
              className={`btn btn-${this.themeClass} btn-lg dropdown-toggle dropdown-toggle-split`}
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              <span className="sr-only">Toggle Theme Dropdown</span>
            </button>

            <div className="dropdown-menu">
              <a
                className="dropdown-item"
                href="#"
                onClick={e => this.chooseTheme("Primary", e)}
              >
                Primary Theme
              </a>
              <a
                className="dropdown-item"
                href="#"
                onClick={e => this.chooseTheme("Danger", e)}
              >
                Danger Theme
              </a>
              <a
                class="dropdown-item"
                href="#"
                onClick={e => this.chooseTheme("Success", e)}
              >
                Success Theme
              </a>

              <div className="dropdown-divider"></div>

              <a className="dropdown-item" href="#" onClick={this.resetTheme}>
                Default Theme
              </a>
            </div>
          </div>
        </div>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
              <a className="nav-link" href="/loads">
                Loads<span className="sr-only">(current)</span>
              </a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="/users">
                Users
              </a>
            </li>

            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="#"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
              >
                Add User
              </a>
              <div className="dropdown-menu" aria-labelledby="navbarDropdown">
                <a className="dropdown-item" href="">
                  Driver
                </a>
                <a className="dropdown-item" href="">
                  Accountant
                </a>
                <a className="dropdown-item" href="">
                  Dispatch
                </a>
                <div className="dropdown-divider"></div>
                <a className="dropdown-item" href="">
                  Something else here
                </a>
              </div>
            </li>
          </ul>
        </div>
        <FormSearch
          handleSubmit={this.handleSubmit}
          handleInputChange={this.handleInputChange}
        />
      </nav>
    );
  }
}

export default NavBar;
